    private void bdnAddActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bdnAddActionPerformed
        Produto p1 = new Produto(txtNome.getText(), Double.parseDouble(txtPreco.getText()), Integer.parseInt(txtQntd.getText()));
        GerenciadorProdutos g1 = new GerenciadorProdutos();
        System.out.println(p1.CalcularTotal());
        System.out.println(g1.listarProdutos());
        g1.adicionarProduto(p1);
       // System.out.println(g1.adicionarProduto(p1.)));
        txtArea.setText(g1.listarProdutos());
    }//GEN-LAST:event_bdnAddActionPerformed

